import React from 'react';
import Dialog from '@mui/material/Dialog';
import DialogTitle from '@mui/material/DialogTitle';
import DialogContent from '@mui/material/DialogContent';
import DialogContentText from '@mui/material/DialogContentText';
import DialogActions from '@mui/material/DialogActions';
import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button';

function Absence({ open1, handleClose1, handleConfirmAddEmployee1, employeeInfo1, handleInputChange1 }) {
  return (
    <Dialog open={open1} onClose={handleClose1}>
      <DialogTitle>Lập đơn nghỉ</DialogTitle>
      <DialogContent>
        <DialogContentText>
          Nhập thông tin cần thiết:
        </DialogContentText>
        <TextField
          autoFocus
          margin="dense"
          id="name"
          name="name"
          label="Tên nhân viên"
          type="text"
          fullWidth
          value={employeeInfo1.name}
          onChange={handleInputChange1}
        />
        <TextField
          margin="dense"
          id="code"
          name="code"
          label="Mã nhân viên"
          type="text"
          fullWidth
          value={employeeInfo1.code}
          onChange={handleInputChange1}
        />
        <TextField
          margin="dense"
          id="email"
          name="email"
          label="Email"
          type="email"
          fullWidth
          value={employeeInfo1.email}
          onChange={handleInputChange1}
        />
        <TextField
          margin="dense"
          id="position"
          name="position"
          label="Vị trí"
          type="text"
          fullWidth
          value={employeeInfo1.position}
          onChange={handleInputChange1}
        />
        <TextField
          margin="dense"
          id="status"
          name="status"
          label="Trạng thái"
          type="text"
          fullWidth
          value={employeeInfo1.status}
          onChange={handleInputChange1}
        />
      </DialogContent>
      <DialogActions>
        <Button onClick={handleClose1}>Hủy</Button>
        <Button onClick={handleConfirmAddEmployee1}>Xác nhận</Button>
      </DialogActions>
    </Dialog>
  );
}

export default Absence;
