import React, { useState, useEffect } from 'react';
import { styled, useTheme } from '@mui/material/styles';

import Box from '@mui/material/Box';
import MuiDrawer from '@mui/material/Drawer';
import MuiAppBar from '@mui/material/AppBar';
import Toolbar from '@mui/material/Toolbar';
import List from '@mui/material/List';
import CssBaseline from '@mui/material/CssBaseline';
import Typography from '@mui/material/Typography';
import Divider from '@mui/material/Divider';
import IconButton from '@mui/material/IconButton';
import MenuIcon from '@mui/icons-material/Menu';
import ChevronLeftIcon from '@mui/icons-material/ChevronLeft';
import ChevronRightIcon from '@mui/icons-material/ChevronRight';
import ListItem from '@mui/material/ListItem';
import ListItemButton from '@mui/material/ListItemButton';
import ListItemIcon from '@mui/material/ListItemIcon';
import ListItemText from '@mui/material/ListItemText';
import GroupIcon from '@mui/icons-material/Group';
import BusinessIcon from '@mui/icons-material/Business';
import ManageAccountsIcon from '@mui/icons-material/ManageAccounts';
import AccountBalanceWalletIcon from '@mui/icons-material/AccountBalanceWallet';
import Button from '@mui/material/Button';
import Snackbar from '@mui/material/Snackbar';
import MuiAlert from '@mui/material/Alert';
import TextField from '@mui/material/TextField';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogContentText from '@mui/material/DialogContentText';
import DialogTitle from '@mui/material/DialogTitle';
// import Tab from '@mui/material/Tab';
// import Tabs from '@mui/material/Tabs';
import TotalEmployeesTab from './TotalEmployeesTab'; // Import tab tổng nhân viên
import NewEmployeesTab from './NewEmployeesTab';
import './styles.css';
import FactoryIcon from '@mui/icons-material/Factory';
import LocalShippingIcon from '@mui/icons-material/LocalShipping';
import ResignedEmployeesTab from './ResignedEmployeesTab';
import LeaveByPolicyEmployeesTab from './LeaveByPolicyEmployeesTab';
import SearchIcon from '@mui/icons-material/Search';
import InputAdornment from '@mui/material/InputAdornment';
import CompanyLogo from './logo.svg';


import FiberManualRecordIcon from '@mui/icons-material/FiberManualRecord';
import DuyetChungTuTable from './DuyetChungTuTable'; // Import component DuyetChungTuTable
import EmployeeTable from './EmployeeTable';



function Alert(props) {
  return <MuiAlert elevation={6} variant="filled" {...props} />;
}

const drawerWidth = 240;

const openedMixin = (theme) => ({
  width: drawerWidth,
  transition: theme.transitions.create('width', {
    easing: theme.transitions.easing.sharp,
    duration: theme.transitions.duration.enteringScreen,
  }),
  overflowX: 'hidden',
});

const closedMixin = (theme) => ({
  transition: theme.transitions.create('width', {
    easing: theme.transitions.easing.sharp,
    duration: theme.transitions.duration.leavingScreen,
  }),
  overflowX: 'hidden',
  width: `calc(${theme.spacing(7)} + 1px)`,
  [theme.breakpoints.up('sm')]: {
    width: `calc(${theme.spacing(8)} + 1px)`,
  },
});

const DrawerHeader = styled('div')(({ theme }) => ({
  display: 'flex',
  alignItems: 'center',
  justifyContent: 'flex-end',
  padding: theme.spacing(0, 1),
  // necessary for content to be below app bar
  ...theme.mixins.toolbar,
}));

const AppBar = styled(MuiAppBar, {
  shouldForwardProp: (prop) => prop !== 'open',
})(({ theme, open }) => ({
  zIndex: theme.zIndex.drawer + 1,
  transition: theme.transitions.create(['width', 'margin'], {
    easing: theme.transitions.easing.sharp,
    duration: theme.transitions.duration.leavingScreen,
  }),
  ...(open && {
    marginLeft: drawerWidth,
    width: `calc(100% - ${drawerWidth}px)`,
    transition: theme.transitions.create(['width', 'margin'], {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.enteringScreen,
    }),
  }),
}));

const Drawer = styled(MuiDrawer, { shouldForwardProp: (prop) => prop !== 'open' })(
  ({ theme, open }) => ({
    width: drawerWidth,
    flexShrink: 0,
    whiteSpace: 'nowrap',
    boxSizing: 'border-box',
    ...(open && {
      ...openedMixin(theme),
      '& .MuiDrawer-paper': openedMixin(theme),
    }),
    ...(!open && {
      ...closedMixin(theme),
      '& .MuiDrawer-paper': closedMixin(theme),
    }),
  }),
);



// const countNewEmployees = async () => {
//   try {
//     const response = await fetch('/api/countNewEmployees');
//     if (!response.ok) {
//       throw new Error('Failed to count new employees');
//     }
//     const count = await response.json();
//     return count;
//   } catch (error) {
//     console.error('Error counting new employees:', error);
//     return 0; // Trả về 0 nếu có lỗi
//   }
// }
//
// const countResignedEmployees = (rows) => {
//   return rows.filter((row) => row.status === 'Nhân viên đã nghỉ').length;
// };
//
// const countLeaveByPolicyEmployees = (rows) => {
//   return rows.filter((row) => row.status === 'Nghỉ theo chế độ').length;
// };

export default function MiniDrawer() {
  const [employees, setEmployees] = useState([]); // State để lưu trữ dữ liệu nhân viên từ API

  useEffect(() => {
    // Gọi API để lấy dữ liệu nhân viên khi component được render
    fetch('api/employees')
        .then(response => response.json())
        .then(data => setEmployees(data))
        .catch(error => console.error('Error fetching data:', error));
  }, []);

  const theme = useTheme();
  const [open, setOpen] = React.useState(false);
  const [snackbarOpen, setSnackbarOpen] = React.useState(false);
  const [snackbarMessage, setSnackbarMessage] = React.useState('');
  const [dialogOpen, setDialogOpen] = React.useState(false);
  const [employeeInfo, setEmployeeInfo] = React.useState({
    employeeName: '',
    employeeCode: '',
    email: '',
    position: '',
    status: '',
  });

  const [totalEmployees, setTotalEmployees] = React.useState(0);
  const [newEmployeesCount, setNewEmployeesCount] = React.useState(0);
  const [resignedEmployeesCount, setResignedEmployeesCount] = React.useState(0);
  const [leaveByPolicyEmployeesCount, setLeaveByPolicyEmployeesCount] = React.useState(0);



  React.useEffect(() => {
    // Gọi API để lấy tổng số nhân viên
    fetch('/api/employees')
        .then(response => response.json())
        .then(data => setTotalEmployees(data))
        .catch(error => console.error('Error fetching total employees:', error));

    // Gọi API để lấy số lượng nhân viên mới
    fetch('/api/employees/countNewEmployees')
        .then(response => response.json())
        .then(data => setNewEmployeesCount(data))
        .catch(error => console.error('Error fetching new employees count:', error));

    // Gọi API để lấy số lượng nhân viên đã nghỉ
    fetch('/api/employees/countFiredEmployees')
        .then(response => response.json())
        .then(data => setResignedEmployeesCount(data))
        .catch(error => console.error('Error fetching resigned employees count:', error));

    // Gọi API để lấy số lượng nhân viên nghỉ theo chế độ
    fetch('/api/employees/countLeaveByPolicyEmployees')
        .then(response => response.json())
        .then(data => setLeaveByPolicyEmployeesCount(data))
        .catch(error => console.error('Error fetching leave by policy employees count:', error));
  }, []);

  const [searchKeyword, setSearchKeyword] = React.useState(''); // State lưu từ khóa tìm kiếm
  const menuItems = [
    { title: 'Quản lý nhân sự', icon: <GroupIcon /> },
    { title: 'Duyệt chứng từ'},
    { title: 'Kinh doanh', icon: <BusinessIcon /> },
    { title: 'Hành chính nhân sự', icon: <ManageAccountsIcon /> },
    { title: 'Kế toán', icon: <AccountBalanceWalletIcon /> },
    { title: 'Quản lý sản xuất', icon: <FactoryIcon /> },
    { title: 'Logistic', icon: <LocalShippingIcon /> },
  ];
  const [title, setTitle] = React.useState(menuItems[0].title); // Lấy tiêu đề mặc định từ mảng menuTitles
  const [subMenuOpen, setSubMenuOpen] = React.useState(false);

  const [showDuyetChungTuTable, setShowDuyetChungTuTable] = useState(false);





  const handleDrawerOpen = () => {
    setOpen(true);
  };

  const handleDrawerClose = () => {
    setOpen(false);
  };

  const handleAddEmployee = () => {
    setDialogOpen(true);
  };

  const handleSubMenuToggle = (menuItem) => {
    // Cập nhật title khi click vào menuItem
    setTitle(menuItem.title);

    // Kiểm tra title của menuItem
    switch (menuItem.title) {
      case 'Quản lý nhân sự':
        setSubMenuOpen(true);
        setShowTableContainer(true); // Hiển thị table-container khi chọn submenu "Quản lý nhân sự"
        setShowDuyetChungTuTable(false); // Ẩn bảng "Duyệt chứng từ"
        break;
      case 'Duyệt chứng từ':
        setSubMenuOpen(true);
        setShowTableContainer(false); // Ẩn table-container khi chọn submenu "Duyệt chứng từ"
        setShowDuyetChungTuTable(true); // Hiển thị bảng "Duyệt chứng từ"
        break;
      default:
        setSubMenuOpen(false); // Ẩn submenu khi click vào các title khác
        setShowTableContainer(false); // Ẩn table-container khi click vào các title khác
        setShowDuyetChungTuTable(false); // Ẩn bảng "Duyệt chứng từ" khi click vào các title khác
        break;
    }
  };









  const handleDeleteEmployee = (name) => {
    // Xóa nhân viên từ danh sách
    setSnackbarMessage(`Đã xóa nhân viên ${name}`);
    setSnackbarOpen(true);
  };

  const handleEditEmployee = (name) => {
    // Thêm mã logic để chỉnh sửa thông tin nhân viên ở đây
    setSnackbarMessage(`Đã chỉnh sửa nhân viên ${name}`);
    setSnackbarOpen(true);
  };

  const handleDialogClose = () => {
    setDialogOpen(false);
    setEmployeeInfo({
      employeeName: '',
      employeeCode: '',
      email: '',
      position: '',
      status: '',
    });
  };

  const handleConfirmAddEmployee = () => {
    // Thêm mã logic để thêm nhân viên vào bảng ở đây
    setSnackbarMessage('Thêm nhân viên thành công');
    setSnackbarOpen(true);
    setDialogOpen(false);
    setEmployeeInfo({
      employeeName: '',
      employeeCode: '',
      email: '',
      position: '',
      status: '',
    });
  };

  const handleCloseSnackbar = () => {
    setSnackbarOpen(false);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setEmployeeInfo((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleSearch = (e) => {
    setSearchKeyword(e.target.value);
    // Đồng thời có thể thêm mã logic để lọc danh sách nhân viên theo từ khóa tìm kiếm ở đây
  };




  return (
    <Box sx={{ display: 'flex' }}>

      <CssBaseline />
      <AppBar position="fixed" open={open}>
        <Toolbar>
          <IconButton
            color="inherit"
            aria-label="open drawer"
            onClick={handleDrawerOpen}
            edge="start"
            sx={{
              marginRight: 5,
              ...(open && { display: 'none' }),
            }}
          >
            <MenuIcon />
          </IconButton>

          <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
            <Typography variant="h6" noWrap component="div">
              {title} {/* Hiển thị tiêu đề */}
            </Typography>
          </Box>
        </Toolbar>
      </AppBar>

      <Drawer variant="permanent" open={open}>
        <DrawerHeader>
          <IconButton onClick={handleDrawerClose}>
           {theme.direction === 'rtl' ? <ChevronRightIcon /> : <ChevronLeftIcon />}
          </IconButton>
        </DrawerHeader>
        <img src={CompanyLogo} alt="Company Logo" width="200" height="200" style={{ marginTop: '-125px' }} />
        <Divider sx={{ marginTop: '-65px' }} />


        <List>
          {menuItems.map((menuItem, index) => (
            <React.Fragment key={menuItem.title}>
              <ListItem key={menuItem.title} disablePadding sx={{ display: 'block' }}>
                <ListItemButton
                  sx={{
                    minHeight: 48,
                    justifyContent: open ? 'initial' : 'center',
                    px: 2.5,
                  }}
                  onClick={() => {
                    if (menuItem.title === 'Quản lý nhân sự') {
                      handleSubMenuToggle(menuItem);
                    }
                  }}
                >
                  <ListItemIcon
                    sx={{
                      minWidth: 0,
                      mr: open ? 3 : 'auto',
                      justifyContent: 'center',
                    }}
                  >
                    {menuItem.icon}
                  </ListItemIcon>
                  <ListItemText primary={menuItem.title} sx={{ opacity: open ? 1 : 0 }} />
                </ListItemButton>
                {subMenuOpen && title === menuItem.title && (
                  <List sx={{ paddingLeft: 3 }}>
                    <ListItemButton >
                      {/* Thêm các mục con ở đây */}
                      <ListItemIcon>
                        <FiberManualRecordIcon sx={{ fontSize: 15, color: 'black' }} />
                      </ListItemIcon>
                      <ListItemText primary="Duyệt chứng từ" />
                    </ListItemButton>
                    <ListItemButton>
                      <ListItemIcon>
                        <FiberManualRecordIcon sx={{ fontSize: 15, color: 'black' }} />
                      </ListItemIcon>
                      <ListItemText primary="Bảng chấm công" />
                    </ListItemButton>
                    <ListItemButton>
                      <ListItemIcon>
                        <FiberManualRecordIcon sx={{ fontSize: 15, color: 'black' }} />
                      </ListItemIcon>
                      <ListItemText primary="Vi phạm" />
                    </ListItemButton>
                    <ListItemButton>
                      <ListItemIcon>
                        <FiberManualRecordIcon sx={{ fontSize: 15, color: 'black' }} />
                      </ListItemIcon>
                      <ListItemText primary="Đơn nghỉ phép" />
                    </ListItemButton>
                  </List>
                )}
              </ListItem>
            </React.Fragment>
        ))}

        </List>

        <Divider />
      </Drawer>
      <Box component="main" sx={{ flexGrow: 1, p: 3 }}>
        <DrawerHeader />
        <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginTop: '24px' }}>
          <Button variant="contained" onClick={handleAddEmployee}>Thêm nhân viên</Button>
          <TextField
            label="Tìm kiếm"
            variant="outlined"
            size="small"
            value={searchKeyword}
            onChange={handleSearch}
            InputProps={{
              startAdornment: (
                <InputAdornment position="start">
                  <SearchIcon />
                </InputAdornment>
              ),
            }}
            sx={{ marginLeft: 2 }}
          />
        </Box>


        {/* <Box sx={{ mt: 3, ml: '20px' }}>
          <Tabs value={0} aria-label="employee-tabs">
            <Tab label="Tổng nhân viên" />
          </Tabs>
        </Box>
        <Box sx={{ ml: '20px' }}>
          <Typography variant="subtitle1" gutterBottom>
            Tổng số nhân viên: {totalEmployees}
          </Typography>
        </Box> */}
     <Box sx={{ display: 'flex', alignItems: 'center', marginTop: '16px' }}>
        <div style={{ marginRight: '24px' }}>
          <TotalEmployeesTab totalEmployees={totalEmployees} />
        </div>
        <div style={{ marginLeft: '24px' }}>
          <NewEmployeesTab newEmployeesCount={newEmployeesCount} />
        </div>
        <div style={{ marginLeft: '24px' }}>
          <ResignedEmployeesTab resignedEmployeesCount={resignedEmployeesCount} />
        </div>
        <div style={{ marginLeft: '24px' }}>
          <LeaveByPolicyEmployeesTab leaveByPolicyEmployeesCount={leaveByPolicyEmployeesCount} />
        </div>
     </Box>


        <Box sx={{ overflow: 'auto', marginTop: '24px' }}>
        <EmployeeTable employees={employees} handleEditEmployee={handleEditEmployee} handleDeleteEmployee={handleDeleteEmployee} />
        {showDuyetChungTuTable && <DuyetChungTuTable />}












</Box>
      </Box>
      <Dialog open={dialogOpen} onClose={handleDialogClose}>
        <DialogTitle>Thêm nhân viên mới</DialogTitle>
        <DialogContent>
          <DialogContentText>
            Nhập thông tin nhân viên mới:
          </DialogContentText>
          <TextField
            autoFocus
            margin="dense"
            id="name"
            name="name"
            label="Tên nhân viên"
            type="text"
            fullWidth
            value={employeeInfo.name}
            onChange={handleInputChange}
          />
          <TextField
            margin="dense"
            id="code"
            name="code"
            label="Mã nhân viên"
            type="text"
            fullWidth
            value={employeeInfo.code}
            onChange={handleInputChange}
          />
          <TextField
            margin="dense"
            id="email"
            name="email"
            label="Email"
            type="email"
            fullWidth
            value={employeeInfo.email}
            onChange={handleInputChange}
          />
          <TextField
            margin="dense"
            id="position"
            name="position"
            label="Vị trí"
            type="text"
            fullWidth
            value={employeeInfo.position}
            onChange={handleInputChange}
          />
          <TextField
            margin="dense"
            id="status"
            name="status"
            label="Trạng thái"
            type="text"
            fullWidth
            value={employeeInfo.status}
            onChange={handleInputChange}
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={handleDialogClose}>Hủy</Button>
          <Button onClick={handleConfirmAddEmployee}>Xác nhận</Button>
        </DialogActions>
      </Dialog>
      <Snackbar open={snackbarOpen} autoHideDuration={6000} onClose={handleCloseSnackbar}>
        <Alert onClose={handleCloseSnackbar} severity="success" sx={{ width: '100%' }}>
          {snackbarMessage}
        </Alert>
      </Snackbar>

    </Box>
  );
}
