import React, { useState } from 'react';
import Dialog from '@mui/material/Dialog';
import DialogTitle from '@mui/material/DialogTitle';
import DialogContent from '@mui/material/DialogContent';
import DialogContentText from '@mui/material/DialogContentText';
import DialogActions from '@mui/material/DialogActions';
import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button';
import Box from '@mui/material/Box';
import MenuItem from '@mui/material/MenuItem';
import Select from '@mui/material/Select';
import InputLabel from '@mui/material/InputLabel';

function Vipham({ open2, handleClose2, employeeInfo2 }) {
  const [donType, setDonType] = useState('');

  return (
    <Dialog open={open2} onClose={handleClose2}>
      <DialogTitle>Xét vi phạm của nhân viên</DialogTitle>
      <DialogContent>
        <DialogContentText>
          Nhập thông tin cần thiết:
        </DialogContentText>
        <TextField
          autoFocus
          margin="dense"
          id="name"
          name="name"
          label="Mã nhân viên"
          type="text"
          fullWidth
          value={employeeInfo2.name}
        />
        <TextField
          margin="dense"
          id="code"
          name="code"
          label="Tên nhân viên"
          type="text"
          fullWidth
          value={employeeInfo2.code}
        />
        <InputLabel id="donType-label">Loại vi phạm</InputLabel>
        <Select
          labelId="donType-label"
          id="donType"
          value={donType}
          onChange={(e) => setDonType(e.target.value)}
          fullWidth
        >
          <MenuItem value="Đơn nghỉ phép">Đơn nghỉ phép</MenuItem>
          <MenuItem value="Đơn nghỉ hưu">Đơn nghỉ hưu</MenuItem>
          <MenuItem value="Đơn nghỉ chế độ">Đơn nghỉ chế độ</MenuItem>
        </Select>
        <TextField
          margin="dense"
          id="position"
          name="position"
          label="Mức độ"
          type="text"
          fullWidth
          value={employeeInfo2.position}
        />
        <TextField
          margin="dense"
          id="status"
          name="status"
          label="Tình trạng"
          type="text"
          fullWidth
          value={employeeInfo2.status}
        />
        
      </DialogContent>
      <DialogActions>
        <Box sx={{ display: 'flex', gap: 2 }}>
          <Button variant="contained" onClick={handleClose2}>Xác nhận</Button>
        </Box>
      </DialogActions>
    </Dialog>
  );
}

export default Vipham;
