import React from 'react';
import Paper from '@mui/material/Paper';
import Table from '@mui/material/Table';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableBody from '@mui/material/TableBody';
import TableRow from '@mui/material/TableRow';

import IconButton from '@mui/material/IconButton';
import EditIcon from '@mui/icons-material/Edit';
import DeleteIcon from '@mui/icons-material/Delete';
import { styled } from '@mui/material/styles'; // Import styled từ @mui/material/styles

const EmployeeTable = ({ employees, handleEditEmployee, handleDeleteEmployee }) => {
    const HighlightedTableCell = styled(HighlightedTableCell)(({ theme }) => ({
        backgroundColor: theme.palette.primary.main,
        color: theme.palette.primary.contrastText,
      }));
  return ( 
    <div className="table-container">
      <TableContainer component={Paper} sx={{ maxHeight: 'calc(100vh - 200px)' }}>
        <Table sx={{ minWidth: 650, overflow: 'auto' }} aria-label="simple table">
          <TableHead>
            <TableRow>
              <HighlightedTableCell align="left">Tên nhân viên</HighlightedTableCell>
              <HighlightedTableCell align="left">Mã nhân viên</HighlightedTableCell>
              <HighlightedTableCell align="left">Email</HighlightedTableCell>
              <HighlightedTableCell align="left">Vị trí</HighlightedTableCell>
              <HighlightedTableCell align="left">Trạng thái</HighlightedTableCell>
              <HighlightedTableCell align="left">Thao tác</HighlightedTableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {employees.map((row) => (
              <TableRow key={row.employeeName}>
                <HighlightedTableCell align="left">{row.employeeName}</HighlightedTableCell>
                <HighlightedTableCell align="left">{row.employeeCode}</HighlightedTableCell>
                <HighlightedTableCell align="left">{row.email}</HighlightedTableCell>
                <HighlightedTableCell align="left">{row.position}</HighlightedTableCell>
                <HighlightedTableCell align="left">{row.status}</HighlightedTableCell>
                <HighlightedTableCell align="left">
                  <IconButton aria-label="edit" onClick={() => handleEditEmployee(row.name)}>
                    <EditIcon />
                  </IconButton>
                  <IconButton aria-label="delete" onClick={() => handleDeleteEmployee(row.name)}>
                    <DeleteIcon />
                  </IconButton>
                </HighlightedTableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </div>
  );
};

export default EmployeeTable;
